// Comprehensive lead scoring algorithm for Hampstead Renovations
// Factors: Location, Budget, Project Fit, Timeline, Engagement, Source Quality

const lead = $input.item.json;

// Initialize scoring object
const scoring = {
  factors: {},
  totalScore: 0,
  maxScore: 100,
  qualification: '',
  priority: '',
  reasoning: [],
  recommendations: []
};

// === FACTOR 1: LOCATION SCORING (Max 25 points) ===
const locationScore = () => {
  let score = 0;
  const targetPostcodes = ['NW3', 'NW6', 'NW11', 'NW8', 'NW1', 'N6', 'N2'];
  const secondaryPostcodes = ['NW2', 'NW4', 'NW5', 'NW7', 'NW9', 'NW10', 'N3', 'N10', 'N12'];
  const location = (lead.location_hints || lead.postcode || lead.area || '').toUpperCase();

  // Check for target postcodes
  for (const pc of targetPostcodes) {
    if (location.includes(pc)) {
      score = 25;
      scoring.reasoning.push(`âœ“ Prime location: ${pc} (our target area)`);
      return score;
    }
  }

  // Check for secondary postcodes
  for (const pc of secondaryPostcodes) {
    if (location.includes(pc)) {
      score = 18;
      scoring.reasoning.push(`â—‹ Secondary location: ${pc} (serviceable area)`);
      return score;
    }
  }

  // Check for North London generally
  if (location.includes('NW') || location.includes('HAMPSTEAD') || location.includes('HIGHGATE')) {
    score = 15;
    scoring.reasoning.push('â—‹ North West London area');
  } else if (location.includes('LONDON') || location.includes('N ')) {
    score = 10;
    scoring.reasoning.push('â–³ Greater London (may require assessment)');
  } else if (location) {
    score = 5;
    scoring.reasoning.push('â–³ Location outside typical service area');
  } else {
    score = 8; // Unknown location - neutral
    scoring.reasoning.push('? Location unknown - needs confirmation');
    scoring.recommendations.push('Confirm property location during callback');
  }

  return score;
};

// === FACTOR 2: PROJECT FIT SCORING (Max 25 points) ===
const projectFitScore = () => {
  let score = 0;
  const projectTypes = lead.project_types || [];
  const complexity = lead.project_complexity || lead.complexity || '';

  // High-value project types for Hampstead Renovations
  const premiumProjects = ['kitchen', 'bathroom', 'extension', 'loft', 'basement'];
  const standardProjects = ['refurbishment', 'structural', 'renovation'];
  const smallProjects = ['decoration', 'painting', 'minor_repairs'];

  let hasPremium = false;
  let hasStandard = false;

  for (const project of projectTypes) {
    const p = project.toLowerCase();
    if (premiumProjects.some(pp => p.includes(pp))) hasPremium = true;
    if (standardProjects.some(sp => p.includes(sp))) hasStandard = true;
  }

  if (hasPremium) {
    score = 20;
    scoring.reasoning.push(`âœ“ Premium project type: ${projectTypes.join(', ')}`);
  } else if (hasStandard) {
    score = 15;
    scoring.reasoning.push(`â—‹ Standard renovation project`);
  } else if (projectTypes.length > 0) {
    score = 8;
    scoring.reasoning.push(`â–³ Smaller scope project`);
  } else {
    score = 10;
    scoring.reasoning.push('? Project type not specified');
    scoring.recommendations.push('Clarify project scope during consultation');
  }

  // Complexity bonus
  if (complexity.toLowerCase() === 'complex' || complexity.toLowerCase() === 'major') {
    score += 5;
    scoring.reasoning.push('âœ“ Complex project (higher value potential)');
  }

  return Math.min(25, score);
};

// === FACTOR 3: BUDGET INDICATOR SCORING (Max 20 points) ===
const budgetScore = () => {
  let score = 0;
  const budget = (lead.budget_indicator || lead.budget || '').toLowerCase();
  const budgetRange = lead.budget_range || '';

  // Parse budget ranges if provided
  if (budgetRange) {
    const numericBudget = parseInt(budgetRange.replace(/[^0-9]/g, ''));
    if (numericBudget >= 100000) {
      score = 20;
      scoring.reasoning.push(`âœ“ High budget: ${budgetRange}`);
    } else if (numericBudget >= 50000) {
      score = 16;
      scoring.reasoning.push(`âœ“ Good budget: ${budgetRange}`);
    } else if (numericBudget >= 20000) {
      score = 12;
      scoring.reasoning.push(`â—‹ Moderate budget: ${budgetRange}`);
    } else {
      score = 6;
      scoring.reasoning.push(`â–³ Limited budget: ${budgetRange}`);
    }
  } else if (budget.includes('premium') || budget.includes('high') || budget.includes('flexible')) {
    score = 18;
    scoring.reasoning.push('âœ“ Premium budget indicator');
  } else if (budget.includes('mid') || budget.includes('moderate')) {
    score = 12;
    scoring.reasoning.push('â—‹ Mid-range budget indicator');
  } else if (budget.includes('budget') || budget.includes('tight') || budget.includes('limited')) {
    score = 5;
    scoring.reasoning.push('â–³ Budget-conscious lead');
    scoring.recommendations.push('Discuss budget expectations early');
  } else {
    score = 10;
    scoring.reasoning.push('? Budget not indicated');
  }

  return score;
};

// === FACTOR 4: TIMELINE/URGENCY SCORING (Max 15 points) ===
const timelineScore = () => {
  let score = 0;
  const urgency = (lead.urgency || lead.timeline || '').toLowerCase();

  if (urgency.includes('urgent') || urgency.includes('asap') || urgency.includes('immediate')) {
    score = 15;
    scoring.reasoning.push('âœ“ Urgent timeline - ready to proceed');
    scoring.recommendations.push('Prioritize callback - hot lead');
  } else if (urgency.includes('soon') || urgency.includes('1-3 months') || urgency.includes('this month')) {
    score = 12;
    scoring.reasoning.push('âœ“ Near-term timeline');
  } else if (urgency.includes('flexible') || urgency.includes('planning')) {
    score = 8;
    scoring.reasoning.push('â—‹ Flexible timeline');
  } else if (urgency.includes('next year') || urgency.includes('long term')) {
    score = 4;
    scoring.reasoning.push('â–³ Long-term planning');
    scoring.recommendations.push('Add to nurture sequence');
  } else {
    score = 7;
    scoring.reasoning.push('? Timeline not specified');
  }

  return score;
};

// === FACTOR 5: ENGAGEMENT QUALITY SCORING (Max 10 points) ===
const engagementScore = () => {
  let score = 0;

  // Source quality
  const source = (lead.source || lead.lead_source || '').toLowerCase();

  if (source.includes('referral') || source.includes('recommendation')) {
    score += 5;
    scoring.reasoning.push('âœ“ Referral lead (high conversion potential)');
  } else if (source.includes('website') || source.includes('webform')) {
    score += 4;
    scoring.reasoning.push('âœ“ Website lead (intentional inquiry)');
  } else if (source.includes('whatsapp')) {
    score += 4;
    scoring.reasoning.push('â—‹ WhatsApp lead');
  } else if (source.includes('voicemail')) {
    score += 3;
    scoring.reasoning.push('â—‹ Voicemail lead');
  } else {
    score += 2;
  }

  // Information completeness
  let infoScore = 0;
  if (lead.name) infoScore++;
  if (lead.email) infoScore++;
  if (lead.phone) infoScore++;
  if (lead.project_types?.length > 0) infoScore++;
  if (lead.location_hints || lead.postcode) infoScore++;

  score += Math.min(5, infoScore);

  if (infoScore >= 4) {
    scoring.reasoning.push('âœ“ Complete contact information');
  } else if (infoScore <= 2) {
    scoring.recommendations.push('Collect missing contact details');
  }

  return Math.min(10, score);
};

// === FACTOR 6: PROPERTY TYPE SCORING (Max 5 points) ===
const propertyScore = () => {
  let score = 0;
  const propertyType = (lead.property_type || '').toLowerCase();

  if (propertyType.includes('period') || propertyType.includes('victorian') || propertyType.includes('edwardian')) {
    score = 5;
    scoring.reasoning.push('âœ“ Period property (specialist expertise)');
  } else if (propertyType.includes('house') || propertyType.includes('detached') || propertyType.includes('semi')) {
    score = 4;
    scoring.reasoning.push('âœ“ House property');
  } else if (propertyType.includes('flat') || propertyType.includes('apartment')) {
    score = 3;
    scoring.reasoning.push('â—‹ Flat/apartment');
  } else if (propertyType.includes('new_build')) {
    score = 2;
    scoring.reasoning.push('â–³ New build (less renovation typically needed)');
  } else {
    score = 2;
  }

  return score;
};

// Calculate all scores
scoring.factors.location = locationScore();
scoring.factors.projectFit = projectFitScore();
scoring.factors.budget = budgetScore();
scoring.factors.timeline = timelineScore();
scoring.factors.engagement = engagementScore();
scoring.factors.property = propertyScore();

// Calculate total score
scoring.totalScore = Object.values(scoring.factors).reduce((sum, val) => sum + val, 0);

// Determine qualification
if (scoring.totalScore >= 80) {
  scoring.qualification = 'HOT';
  scoring.priority = 'CRITICAL';
} else if (scoring.totalScore >= 65) {
  scoring.qualification = 'WARM';
  scoring.priority = 'HIGH';
} else if (scoring.totalScore >= 45) {
  scoring.qualification = 'COOL';
  scoring.priority = 'MEDIUM';
} else {
  scoring.qualification = 'COLD';
  scoring.priority = 'LOW';
}

// Add qualification-specific recommendations
if (scoring.qualification === 'HOT') {
  scoring.recommendations.unshift('ðŸ”¥ Priority callback within 1 hour');
  scoring.recommendations.push('Fast-track to site survey scheduling');
} else if (scoring.qualification === 'WARM') {
  scoring.recommendations.unshift('ðŸ“ž Callback within 4 hours');
} else if (scoring.qualification === 'COOL') {
  scoring.recommendations.unshift('ðŸ“§ Send information pack, follow up in 2-3 days');
} else {
  scoring.recommendations.unshift('ðŸ“‹ Add to nurture campaign');
}

return {
  json: {
    lead_id: lead.id || lead.lead_id,
    source_id: lead.source_id,
    scoring: scoring,
    scored_at: new Date().toISOString()
  }
};

